import React, { useState } from 'react';

function App() {
  const [greeting, setGreeting] = useState('');
  const [message, setMessage] = useState('');
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);

  // 从Java后端获取问候信息
  const handleGetHello = async () => {
    setLoading(true);
    try {
      const result = await window.electronAPI.getHello();
      setGreeting(result);
    } catch (error) {
      console.error('获取问候失败:', error);
      setGreeting('获取问候失败');
    } finally {
      setLoading(false);
    }
  };

  // 发送消息到Java后端
  const handleSendMessage = async () => {
    if (!message) {
      setResponse('请输入消息');
      return;
    }

    setLoading(true);
    try {
      const result = await window.electronAPI.sendMessage(message);
      setResponse(result.response);
    } catch (error) {
      console.error('发送消息失败:', error);
      setResponse('发送消息失败');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="header">
        <h1>Electron与Java Spring Boot通信示例</h1>
      </div>
      
      <div>
        <h2>获取后端问候</h2>
        <button onClick={handleGetHello} disabled={loading}>
          {loading ? '加载中...' : '获取问候'}
        </button>
        {greeting && (
          <div className="message-container">
            <p>{greeting}</p>
          </div>
        )}
      </div>
      
      <div style={{ marginTop: '30px' }}>
        <h2>发送消息到后端</h2>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="输入要发送的消息"
          disabled={loading}
        />
        <button onClick={handleSendMessage} disabled={loading}>
          {loading ? '发送中...' : '发送消息'}
        </button>
        {response && (
          <div className="message-container">
            <p>{response}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
